import React from 'react';

const FProgressBar = ({ progress }) => {
  const progressBarWidth = 10; // 設置進度條容器的寬度百分比

  const progressBarStyle = {
    background: '#fff',
    height: '1px',
    position: 'absolute',
    top: '97%',
    left: '44.5%',
    width: `${progressBarWidth}%`,
  };

  const progressStyle = {
    background: '#000',
    height: '4px',
    left: '0',
    position: 'absolute',
    transition: 'width 0.3s ease-in-out',
    width: `${(progress * progressBarWidth)}%`, // 使進度條與容器寬度相乘
  };

  return (
    <div style={progressBarStyle}>
      <div style={progressStyle} />
    </div>
  );
};

export default FProgressBar;
