import React from 'react'
import { useParams } from 'react-router-dom';
import moment from 'moment';

import './style/FBlogPost.css'

const FBlogPost = ( props ) => {
  function truncateString(str) {
    var maxLength = 50; // 英文情況下的最大長度
    var suffix = "..."; // 後綴

    // 如果字串以英文開頭，將最大長度設為50
    if (/^[a-zA-Z]/.test(str)) {
      maxLength = 50;
    } else {
      maxLength = 25; // 否則設為15
    }

    // 檢查字串是否超過最大長度
    if (str.length > maxLength) {
      // 將字串截斷到最大長度並加上後綴
      str = str.substring(0, maxLength) + suffix;
    }

    return str;
  }

  return (
    <div className='main'>
        <div className='date'>
            <div className='day'>
              {moment(props.date).format("DD")}
            </div>
            <div className='year-month'>
              {moment(props.date).format("YYYY.MM")}
            </div>
        </div>
        <div className='title-and-content'>
          <div className='title'>
            {props.title}
          </div>
          <div>
            {truncateString(props.ContentEng誒)}
          </div>
        </div>
    </div>
  )
}

export default FBlogPost
