import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import Axios from "axios";
import { Carousel } from 'react-bootstrap';

import LikeButton from '../component/LikeButton';
import { getPost } from '../ApiCaller';

import '../App.css'

const BlogPost = () => {
  const {id}= useParams();

  const [Post, setPost] = useState([]);
  const [PostObj, setPostObj] = useState({});

  useEffect(() => {
    getPost(id).then(data => setPost(data));
  }, [])

  const imageArr = Post.map(obj => obj.ImagePath);
  let updatePostObj;
  Post.map(item => (
    updatePostObj = { PostID: item.PostID, DataAndTime: item.DataAndTime, Title: item.Title}
  ));
  useEffect(() => {
    setPostObj(updatePostObj);
  })

  if (imageArr.length === 1) {
    var imageSection = <img src={imageArr[0]} alt={imageArr[0]} className='PostCarouselSize'/>
  } else {
    var imageSection = 
    <Carousel>
      {imageArr.map((val) => (
        <Carousel.Item>
          <img src={val} alt={val} className='PostCarouselSize'/>
        </Carousel.Item>
      ))} 
    </Carousel>
  }

  return (
    <>
      <div className='PostSection'>
        <div>
          {imageSection}
          <div className='LikeButton'>
            {/* <LikeButton Like={ Post[0].Likes }/> */}
          </div>
          <div className='PostContent'>
            <div>
              {PostObj.Title}
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default BlogPost
