import React from 'react'

const FormInput = ( props ) => {

    var inputType;

    switch (props.type) {
        case "input":
            inputType = 
            <div>
                <input className='FormInput' placeholder={props.placeholder}/>
            </div>
            break;
        case "select":
            inputType =
            <div>
                <select>
                    <option value="EX1">EX1</option>
                    <option value="EX2">EX2</option>
                    <option value="EX3">EX3</option>
                    <option value="EX4">EX4</option>
                    {props.optionArr.map()}
                </select>
            </div>
    }


    return (
        <div>
            {inputType}
        </div>
    )
}

export default FormInput
