import React, { useState, useEffect } from 'react';

import './style/FCarousel.css';

const Carousel = ( props ) => {
  const [currentImage, setCurrentImage] = useState(0);
  const [imagePaths, setImagePaths] = useState([]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImage((currentImage + 1) % props.EventPic.length);
    }, 3000);

    return () => {
      clearInterval(intervalId);
    };
  }, [currentImage]);

  const handlePrev = () => {
    setCurrentImage((currentImage - 1 + props.EventPic.length) % props.EventPic.length);
  };

  const handleNext = () => {
    setCurrentImage((currentImage + 1) % props.EventPic.length);
    console.log(props.EventPic[currentImage])
  };

  props.EventPic.filter(val => val.InPage === 'Carousel').map((val) => (
    <Carousel.Item interval={2000} key={val.id}>
      <img
        className="CarouselHomePage"
        src={val.ImagePath}
        alt={val.id}
      />
      <Carousel.Caption>
      </Carousel.Caption>
    </Carousel.Item>
  ))

  return (
    <div className="carousel">
      <img src={props.EventPic[currentImage]} alt="carousel" />
      <div className="buttons">
        <button onClick={handlePrev}>Prev</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default Carousel;
